mapper.select:
SELECT distinct a.prdt_sn prdt_sn, a.prdt_nm prdt_nm, c.cate_sn cate_sn_3, c.cate_nm lev3 FROM comt_cbntr_product a JOIN comt_cbntr_data b ON a.prdt_sn = b.prdt_sn JOIN comt_cbntr_category c ON a.cate_sn = c.cate_sn JOIN comt_cbntr_category b2 ON b2.cate_sn = c.cate_upr_sn JOIN comt_cbntr_category b3 ON b3.cate_sn = b2.cate_upr_sn WHERE b3.cate_sn = 1
OR c.cate_nm = #{cate_nm}
ORDER BY a.prdt_sn ASC
